// Validate incoming data structure
const data = items[0].json;

if (!data.quantitative || !data.qualitative) {
  throw new Error('Missing required data: quantitative or qualitative');
}

if (!data.sessionId) {
  throw new Error('Missing session ID');
}

console.log('Data validation passed for session:', data.sessionId);

// Structure the data for analysis
const structuredData = {
  sessionId: data.sessionId,
  timestamp: data.timestamp,
  gameMetrics: {
    participationRate: data.quantitative.quantitativeData.participationRate,
    responseTime: data.quantitative.quantitativeData.averageResponseTime,
    engagement: data.quantitative.quantitativeData.emotionalEngagement,
    collaboration: data.quantitative.quantitativeData.collaborationScore,
    playerCount: data.quantitative.playerMetrics.length
  },
  managerObservations: {
    teamDynamics: data.qualitative.managerObservations.teamDynamics,
    communication: data.qualitative.managerObservations.communicationIssues,
    leadership: data.qualitative.managerObservations.leadershipEmergence,
    creativity: data.qualitative.managerObservations.creativityLevel,
    trust: data.qualitative.managerObservations.trustIndicators
  },
  managerInfo: data.qualitative.managerInfo,
  gameInfo: data.quantitative.gameInfo
};

return [{ json: structuredData }];

// Parse AI response and generate report data
const aiResponse = items[0].json.message.content;
const sessionData = items[0].json;

let insights;
try {
  insights = JSON.parse(aiResponse);
} catch (error) {
  // Fallback if AI doesn't return valid JSON
  insights = {
    executiveSummary: aiResponse.substring(0, 200) + '...',
    strengths: ['Strong team participation', 'Good communication', 'Collaborative approach'],
    improvements: ['Time management', 'Leadership clarity', 'Creative thinking'],
    recommendations: ['Weekly check-ins', 'Leadership rotation', 'Skill workshops'],
    followUp: ['Schedule next session', 'Individual coaching', 'Team retrospective']
  };
}

// Generate comprehensive report data
const reportData = {
  sessionId: sessionData.sessionId,
  timestamp: new Date(),
  managerEmail: sessionData.managerInfo.managerEmail,
  managerName: sessionData.managerInfo.managerName,
  gameInfo: sessionData.gameInfo,

  // Metrics summary
  metrics: {
    overallEngagement: Math.round((sessionData.gameMetrics.engagement + sessionData.gameMetrics.collaboration) * 5), // Convert to percentage
    participationRate: sessionData.gameMetrics.participationRate,
    teamCohesion: sessionData.gameMetrics.collaboration * 10,
    communicationScore: Math.max(90 - (sessionData.managerObservations.communication.length / 10), 60), // Inverse of issues length
    leadershipClarity: sessionData.managerObservations.leadership.includes('clear') ? 85 : 70
  },

  // AI insights
  insights: insights,

  // Chart data for dashboard
  chartData: {
    engagement: {
      labels: ['Communication', 'Collaboration', 'Leadership', 'Creativity', 'Trust'],
      values: [
        Math.max(90 - (sessionData.managerObservations.communication.length / 10), 60),
        sessionData.gameMetrics.collaboration * 10,
        sessionData.managerObservations.leadership.includes('clear') ? 85 : 70,
        sessionData.managerObservations.creativity === 'high' ? 90 : sessionData.managerObservations.creativity === 'medium' ? 70 : 50,
        sessionData.managerObservations.trust.length > 50 ? 85 : 65
      ]
    }
  },

  // PDF generation data
  pdfData: {
    title: `Team Analysis Report - ${sessionData.gameInfo.gameName}`,
    date: new Date().toLocaleDateString(),
    summary: insights.executiveSummary,
    metrics: sessionData.gameMetrics,
    observations: sessionData.managerObservations,
    recommendations: insights.recommendations
  }
};

return [{ json: reportData }];
